// Food function here
function chooseFood(location, price) {
  if (location === "North") {
    if (price === "$") {
      return northSideFood1;
    } else if (price === "$$") {
      return northSideFood1;
    } else {
      return northSideFood2;
    }
  } else if (location === "South") {
    if (price === "$") {
      return southSideFood1;
    } else if (price === "$$$") {
      return southSideFood2;
    } else {
      return southSideFood2;
    }
  } else if (location === "East") {
    if (price === "$") {
      return eastSideFood1;
    } else if (price === "$$$") {
      return eastSideFood2;
    } else {
      return eastSideFood2;
    }
  } else {
    if (price === "$") {
      return westSideFood1;
    } else if (price === "$$$") {
      return westSideFood2;
    } else {
      return westSideFood2;
    }
  }
  return westSideFood2;
}

// Art function here
function chooseArt(location, price) {
  if (location === "North") {
    if (price === "$") {
      return northSideArt1;
    } else if (price === "$$") {
      return northSideArt2;
    } else {
      return northSideArt2;
    }
  } else if (location === "East") {
    if (price === "$$$") {
      return eastSideArt1;
    } else if (price === "$$") {
      return eastSideArt2;
    } else {
      return eastSideArt1;
    }
  } else if (location === "South") {
    if (price === "$") {
      return southSideArt1;
    } else if (price === "$$") {
      return southSideArt2;
    } else {
      return southSideArt2;
    }
  } else {
    if (price === "$") {
      return westSideArt1;
    } else if (price === "$$") {
      return westSideArt2;
    } else {
      return westSideArt2;
    }
  }
  return westSideArt2;
}

