var rangeSlider = function () {
  var slider = $(".range-slider"),
    range = $(".range-slider__range"),
    value = $(".range-slider__value");

  slider.each(function () {
    value.each(function () {
      var value = $(this).prev().attr("value");
      $(this).html(value);
    });

    range.on("input", function () {
      $(this).next(value).html(this.value);
    });
  });
};

rangeSlider();
function fillfoodCard(place) {
  $(".pf-food")
    .append(`<p><b>Name:</b> ${place.Name}</p>`)
    .append(`<p><b>Description:</b> ${place.Description}</p>`)
    .append(`<p><b>Price:</b> ${place.Price}</p>`)
    .append(`<p><b>Rating:</b> ${place.Rating}</p>`)
    .append(`<img src="${place.image}" width=300>`);
}

